import React,{useState} from 'react'
import { EDUCATION } from '../../Utils/data'
import EducationCard from './EducationCard/educationCard'
import EducationInfoCard from './EducationInfoCard/educationInfoCard'

function Education() {

    const[selectedEducation,setSelectedEducation] = useState(EDUCATION[0]);

    const handleSelectedEducation = (data) => {
        setSelectedEducation(data);
    }
    return (
        <section className="skills-container">
        <h5>EDUCATION</h5>

        <div className="skills-content">
            <div className="skills">
                {EDUCATION.map((item) =>(
                    <EducationCard key={item.title}
                    iconUrl={item.icon}
                    title={item.title}
                    isActive={selectedEducation.title === item.title}
                    onClick={()=>{
                        handleSelectedEducation(item)
                    }}
                    />
                ))}
            </div>
            <div className="skills-info">
                <EducationInfoCard
                heading={selectedEducation.level}
                skills={selectedEducation.organization}
                />
            </div>
        </div>
        </section>
    )
}

export default Education
